import React from "react";
import { Route, Routes, Link } from "react-router-dom";
import RoutePath from "../router/RoutePath";

const Home = () => {
  let path = window.location.pathname;
  console.log(path);
  const controller = () => {
    if ((path = "")) {
      return <RoutePath></RoutePath>;
    }
  };
  return (
    <div>
      {controller()}
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
      </ul>
    </div>
  );
};

export default Home;
