import React from "react";

function Counter() {
  let number = 0;
  const increaseNumber = (number) => {
    return number++;
  };
  return (
    <div>
      {number}
      <p>
        <button onClick={increaseNumber}>Increase +</button>
        <button>Decrease -</button>
      </p>
    </div>
  );
}

export default Counter;
