import { data } from "./../data/data";

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [employee, setList] = useState(data);

  const [selectedEmployee, setselectedEmployee] = useState({
    id: 0,
    firstName: "",
    lastName: "",
  });

  const values = {
    appName: "Employee App",
    employee,
    setList,
    selectedEmployee,
    setselectedEmployee,
  };

  return <AppContext.Provider value={values}>{children}c</AppContext.Provider>;
};
